mapper.select:
SELECT mid, mpassword, mname, memail, mphone, mtel, mzipcode, maddress1, maddress2, mbirth, mgender, mrefid, mlogintype, mtosno, menabled, mrole, mgrade, mmileage
FROM member
WHERE mid=#{mid}

mapper.update:
UPDATE member
SET mphone = #{mphone}

,memail = #{memail}


,mtel = #{mtel}


,mzipcode = #{mzipcode}
,maddress1 = #{maddress1} 
,maddress2 = #{maddress2}

WHERE mid = #{mid}

